#include "IMP/core/ParticleContainerSet.h"
#include <algorithm>


IMPCORE_BEGIN_NAMESPACE

ParticleContainerSet
::ParticleContainerSet(){
}

ParticleContainerSet::~ParticleContainerSet(){}

bool
ParticleContainerSet
::get_contains_particle(Particle* vt) const {
  for (ParticleContainerConstIterator it= particle_containers_begin();
       it != particle_containers_end(); ++it) {
    if ((*it)->get_contains_particle(vt)) return true;
  }
  return false;
}

void ParticleContainerSet::show(std::ostream &out) const {
  IMP_CHECK_OBJECT(this);
  out << "ParticleContainerSet with "
      << get_number_of_particles()
      << " particles." << std::endl;
}

unsigned int
ParticleContainerSet::get_number_of_particles() const {
  unsigned int sum=0;
  for (ParticleContainerConstIterator it= particle_containers_begin();
       it != particle_containers_end(); ++it) {
    sum+= (*it)->get_number_of_particles();
  }
  return sum;
}

Particle*
ParticleContainerSet::get_particle(unsigned int i) const {
  for (ParticleContainerConstIterator it= particle_containers_begin();
       it != particle_containers_end(); ++it) {
    if ( i >= (*it)->get_number_of_particles()) {
      i-= (*it)->get_number_of_particles();
    } else {
      return (*it)->get_particle(i);
    }
  }
  throw IndexException("out of range");
}



