RemoveInactivePairsOptimizerState
::RemoveInactivePairsOptimizerState(ListPairContainer *c,
                                         std::string name):
  OptimizerState(name),
  c_(c){
}

namespace {
  struct Inactive {
    bool operator()(ParticlePair c) const {
      return IMP::internal::ContainerTraits<ParticlePair>::is_inactive(c);
    }
  };
}

void RemoveInactivePairsOptimizerState::update()
{
  IMP_OBJECT_LOG;
  IMP_LOG(TERSE, "Begin RemoveInactivePairsOptimizerState::update"
          << std::endl);
  IMP_CHECK_OBJECT(c_);
  c_->remove_particle_pairs_if(Inactive());

  IMP_LOG(TERSE, "End RemoveInactivePairsOptimizerState::update"
          << std::endl);
}



void RemoveInactivePairsOptimizerState::show(std::ostream &out) const {
  out << "RemoveInactivePairsOptimizerState" << std::endl;
}

